import { pizzaData } from "../data/data";

function Pizza(props) {
    return (
        <div className="pizza">
            <img
                src={props.photoName}
                alt={props.name}
            />
            <div>
                <h3 heading>{props.name}</h3>
                <p>{props.ingredients}</p>
            </div>
        </div>
    );
}

export default Pizza;
