import Pizza from "./Pizza";
import { pizzaData } from "../data/data";

function Menu() {
    const pizzas = pizzaData;
    return (
        <div className="menu">
            <h2>Our Menu</h2>
            <ul className="pizzaContainer">
                {pizzaData.map((pizza) => (
                    <Pizza
                        name={pizza.name}
                        ingredients={pizza.ingredients}
                        price={pizza.price}
                        photoName={pizza.photoName}
                    />
                ))}
            </ul>
        </div>
    );
}

export default Menu;
