import Pizza from "./Pizza";
import { pizzaData } from "../data/data";

function Menu() {
    return (
        <div className="menu">
            <h2>Our Menu</h2>
            <div className="pizzaContainer">
                {pizzaData.map((pizza) => (
                    <Pizza name={pizza.name} ingredients={pizza.ingredients} />
                ))}
            </div>
        </div>
    );
}

export default Menu;
